mapper.select:
from x_authorization_role

     and id = #{id,jdbcType=BIGINT}  
     and remark = #{remark,jdbcType=VARCHAR}  
     and seq = #{seq,jdbcType=BIGINT}  
     and create_time = #{createTime,jdbcType=TIMESTAMP} :: timestamp with time zone 
     and code = #{code,jdbcType=VARCHAR}  
     and name = #{name,jdbcType=VARCHAR}  
     and role_group = #{roleGroup,jdbcType=VARCHAR}  
     and (extended_field->>'param')::text = #{param,jdbcType=VARCHAR} 
     and update_time = #{updateTime,jdbcType=TIMESTAMP} :: timestamp with time zone 
     and parent_id = #{parentId,jdbcType=BIGINT}  
     and left_value = #{leftValue,jdbcType=BIGINT}  
     and right_value = #{rightValue,jdbcType=BIGINT}  
     and parent_node = #{parentNode,jdbcType=VARCHAR}  
     and left_node = #{leftNode,jdbcType=VARCHAR}  
     and right_node = #{rightNode,jdbcType=VARCHAR}  
     and role_id = #{roleId,jdbcType=VARCHAR}  

 order by ${orderby} 
 limit ${limit} 
 offset ${offset}

select count(*) from x_authorization_role

             and id = #{id,jdbcType=BIGINT}  
             and remark = #{remark,jdbcType=VARCHAR}  
             and seq = #{seq,jdbcType=BIGINT}  
             and create_time = #{createTime,jdbcType=TIMESTAMP} :: timestamp with time zone 
             and code = #{code,jdbcType=VARCHAR}  
             and name = #{name,jdbcType=VARCHAR}  
             and role_group = #{roleGroup,jdbcType=VARCHAR}  
             and (extended_field->>'param')::text = #{param,jdbcType=VARCHAR} 
             and update_time = #{updateTime,jdbcType=TIMESTAMP} :: timestamp with time zone 
             and parent_id = #{parentId,jdbcType=BIGINT}  
             and left_value = #{leftValue,jdbcType=BIGINT}  
             and right_value = #{rightValue,jdbcType=BIGINT}  
             and parent_node = #{parentNode,jdbcType=VARCHAR}  
             and left_node = #{leftNode,jdbcType=VARCHAR}  
             and right_node = #{rightNode,jdbcType=VARCHAR}  
             and role_id = #{roleId,jdbcType=VARCHAR}
